import React , {
  NativeModules
} from 'react-native' ;
var RNAudioManager = NativeModules.RNAudioManager ;

export default class AudioRecorder
{
  startRecord(filename)
  {
    RNAudioManager.startRecord(filename);

  }

  pauseRecord()
  {
    RNAudioManager.pauseRecord();
  }

  resumeRecord()
  {
    RNAudioManager.resumeRecord();
  }

  stopRecord()
  {
    RNAudioManager.stopRecord();
  }



  startPlayAtFile(filename)
  {
    RNAudioManager.startPlayAtFile(filename);
  }

  pausePlay()
  {
    RNAudioManager.pausePlay();
  }


  resumePlay()
  {
    RNAudioManager.resumePlay();
  }

  stopPlay()
  {
    RNAudioManager.stopPlay();
  }


};
